	  function animationCanvas(obj,data){
		  //console.log(obj);
		  /**/
		  data = 50;
            var canvas = obj,  //获取canvas元素
                context = canvas.getContext('2d'),  //获取画图环境，指明为2d
                centerX = canvas.width/2,   //Canvas中心点x轴坐标
                centerY = canvas.height/2,  //Canvas中心点y轴坐标
                rad = Math.PI/100, //将360度分成100份，那么每一份就是rad度
                speed = 1; //加载的快慢就靠它了 
				var start = -Math.PI;
				var oParentData = $(obj).parent().attr('data-percent')*1;
				//console.log(oParentData);
				var endValue = oParentData;
            //绘制蓝色外圈
            function blueCircle(n){
                context.save();
                context.strokeStyle = "#ff9429"; //设置描边样式
                context.lineWidth = 4; //设置线宽
                context.beginPath(); //路径开始
                context.arc(centerX, centerY, 60 ,start , start +n*rad, false); //用于绘制圆弧context.arc(x坐标，y坐标，半径，起始角度，终止角度，顺时针/逆时针)
                context.stroke(); //绘制
                context.closePath(); //路径结束
                context.restore();
            }
            //绘制白色外圈
            function whiteCircle(){
                context.save();
				 context.lineWidth = 4;
                context.beginPath();
                context.strokeStyle = "#f0f0f0";
                context.arc(centerX, centerY, 60 , start,start + Math.PI, false);
                context.stroke();
                context.closePath();
                context.restore();
            }  
			
            //百分比文字绘制
            function text(n){
                context.save(); //save和restore可以保证样式属性只运用于该段canvas元素
                context.strokeStyle = "#fff"; //设置描边样式
                context.font = "40px Arial"; //设置字体大小和字体
                //绘制字体，并且指定位置
                context.strokeText(n.toFixed(0)+"%", centerX-25, centerY+10);
                context.stroke(); //执行绘制
                context.restore();
            } 
            //动画循环
            (function drawFrame(){
               var timer =  window.requestAnimationFrame(drawFrame, canvas);
                context.clearRect(0, 0, canvas.width, canvas.height);
                whiteCircle();
               // text(speed);
                blueCircle(speed);
                if(speed > endValue) speed = 0;
                speed += 1;
				if(speed>endValue){
					window.cancelAnimationFrame(timer);
				}
            }());
        }
	 